import React, { Component } from 'react';
import { Card, Form, Button} from 'react-bootstrap';
import styled from 'styled-components';

class RegisterForm extends Component {
    constructor(props) {
        super(props);
        this.state = {email: '', clsName: '', timeout: 1};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        const Styles = styled.div``;

    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({
            [name]: value
        });
        console.log(this.state.email);
    }

    handleSubmit(event) {
        console.log('Submission for ' + this.state.value);
        event.preventDefault();
    }


    render() { return (
        <Card>
            <Card.Header as="h5">Initiate Session</Card.Header>
            <Card.Body>
                <Card.Title>Type your info here</Card.Title>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group>
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email"  name="email" onChange={this.handleChange}></Form.Control>
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Class name</Form.Label>
                        <Form.Control type="text" placeholder="Class name"  name="clsName" onChange={this.handleChange}></Form.Control>
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Timeout</Form.Label>
                        <Form.Control type="number" placeholder="Timeout (minutes)"  name="timeout" onChange={this.handleChange}></Form.Control>
                    </Form.Group>
                    <Button variant="primary" type="submit">Start</Button>
                </Form>
            </Card.Body>
        </Card>
    )
    }
}

export default RegisterForm;
