import { withRouter } from 'react-router-dom';
import React, { Component } from 'react';
import { Card, Form, Button, Alert} from 'react-bootstrap';
import styled from 'styled-components';
import {connect} from 'react-redux';
import SpotifyWebApi from 'spotify-web-api-js';

var spotify = new SpotifyWebApi();


class SpotifyForm extends Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        const Styles = styled.div``;        
    }
    /**
     * Handle change in the artist value
     * @param {Object} event Event incurred by change 
     */
    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.props.change({name: name, value: value});
        if (this.props.vars.recOutput !== undefined) {
            console.log([...this.props.vars.recOutput.entries()].length);
        }
    }
    /**
     * Handle errors by updating the state
     * @param {Object} err 
     */
    handleError(err) {
        switch (err.status) {
            case 401:
                this.props.change({name: "errorStatus", value: "You don't seem to be authorized. Try authorizing with Spotify and trying again."})
                break;
            case 429:
                this.props.change({name: "errorStatus", value: "Looks like we're making too many requests to Spotify right now. Wait a little bit and try that again."})
        }

    }
    /**
     * Gets recommendations for a specific artist and outputs recOutput value in redux store
     * @param {string} name The name of the artist
     * TODO: catch errors again lol
     * /
    **/
    getRecs(name) {
        let map = new Map();
        console.log(name);
        for (let i = 0; i < 3; i++) {
            spotify.searchPlaylists(name, {limit: 1})
            .then(res => res.playlists.total)
            .then(total => spotify.searchPlaylists(name, {limit: 20, offset: Math.floor(Math.random() * (total/50))}).then(
                res => {res.playlists.items
                        // wait in between each request so we don't hit the request limit
                        .forEach(i => setTimeout( () => {
                            spotify.getPlaylistTracks(i.id).then(
                                pt => {
                                    if (pt == undefined) {
                                        return Promise.reject();
                                    }
                                    pt.items.forEach(
                                        track => {
                                            if (track.track != null && track.track.artists[0].name !== name) {
                                                if (map.has(track.track.artists[0].name)) {
                                                    map.set(track.track.artists[0].name, map.get(track.track.artists[0].name) + 1);
                                                } else {
                                                    map.set(track.track.artists[0].name, 1);
                                                }
                                            }
                                        }
                                )
                                })
                        }, 200)  )
                return map;
                }
            )).then(output => { return this.props.change({name: "recOutput", value: map}) } )
            .catch(err => this.handleError(err))
        }
        
    }

    /**
     * Creates playlist with one random song from each output artist
     */
    createPlaylist () {
        let uris = [];

        //spotify.createPlaylist().then((pl) =>
            
       // );
    }

    /**
     *  Handles submission of Artist form
     * @param {Object} e Submission event
     * TODO: Error handling
     */
    handleSubmit(e) {
        e.preventDefault();
        spotify.setAccessToken(this.props.vars.token);
        spotify.searchArtists(this.props.vars.artist)
            .then(res => res.artists.items[0].name)
            .then(name => this.getRecs(name))
            .catch(err => this.handleError(err));
        
    }

    


    render() {
        
            return (
                <React.Fragment>
                {this.props.vars.errorStatus !== undefined ? <Alert className="alert-primary">{this.props.vars.errorStatus}</Alert> : null }
                <Card>
                    <Card.Body>
                        <Card.Title>Enter artist's name for recommendations</Card.Title>
                        <Form onSubmit={this.handleSubmit}>
                            <Form.Group>
                                <Form.Label>Artist</Form.Label>
                                <Form.Control type="text" placeholder="Artist"  name="artist" onChange={this.handleChange} required></Form.Control>
                            </Form.Group>
                            <Form.Group>
                                <Form.Control type="submit" className="bg-primary text-light">
        
                                </Form.Control>
                            </Form.Group>
                        </Form>
                    </Card.Body>
                </Card>
                {this.props.vars.recOutput !== undefined && Object.keys(this.props.vars.recOutput).length > 0 ? <Button className="mt-3" onC={this.createPlaylist()}>Create a playlist with songs from these artists</Button> : null} 
                <ul>{this.props.vars.recOutput !== undefined && Object.keys(this.props.vars.recOutput).length > 0 ? this.props.vars.recOutput.keys().map(band => <li key={band}>{band}: {this.props.vars.recOutput[band]} </li>) : null }</ul>
                </React.Fragment>
                
            )
        
    }
}

// mapping our redux state to box props
const mapStateToProps = (state) => {
    return {
      vars:state.vars
    }
  };
  
  // mapping the dispatches from our reducer to box props 
  const mapDispatchToProps = (dispatch) => {
    return {
      change: (vName) => dispatch( {type: 'CHANGE', name: vName} ),
  
    }
  
  }

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(SpotifyForm));
