import React, { Component } from 'react';
import { Card, Form, Button} from 'react-bootstrap';
import styled from 'styled-components';

class RegisterForm extends Component {
    constructor(props) {
        super(props);
        this.state = {email: '', className: '', timeout: 1};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        const Styles = styled.div``;

    }

    handleChange(event) {
        this.setState({email: event.target.email, className: event.target.className, timeout: event.target.timeout})
    }

    handleSubmit(event) {
        console.log('Submission for ' + this.state.value);
        event.preventDefault();
    }


    render() { return (
        <Card>
            <Card.Header as="h5">Initiate Session</Card.Header>
            <Card.Body>
                <Card.Title>Type your info here</Card.Title>
                <Form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                    <Form.Group>
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="email" placeholder="Email"></Form.Control>
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Class name</Form.Label>
                        <Form.Control type="text" placeholder="Class name"></Form.Control>
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Timeout</Form.Label>
                        <Form.Control type="number" placeholder="Timeout (minutes)"></Form.Control>
                    </Form.Group>
                    <Button variant="primary" type="submit">Start</Button>
                </Form>
            </Card.Body>
        </Card>
    )
    }
}

export default RegisterForm;
