import React, { Component } from 'react';
import {Button} from 'react-bootstrap';
import {connect} from 'react-redux';

export const authEndpoint = 'https://accounts.spotify.com/authorize';
class SpotifyAuth extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isAuthenticatedWithSpotify: false
      // menu: this.props.userId.menu
    };
    this.state.handleRedirect = this.handleRedirect.bind(this);
  }

  componentDidMount() {
    const hash = window.location.hash
    .substring(1)
    .split('&')
    .reduce(function (initial, item) {
      if (item) {
        var parts = item.split('=');
        initial[parts[0]] = decodeURIComponent(parts[1]);
      }
      return initial;
    }, {});

    const access_token = hash.access_token;
    console.log(access_token);
    
    
  }

  handleRedirect(event) {
    event.preventDefault();
    const hash = window.location.hash
    .substring(1)
    .split('&')
    .reduce(function (initial, item) {
      if (item) {
        var parts = item.split('=');
        initial[parts[0]] = decodeURIComponent(parts[1]);
      }
      return initial;
    }, {});
    const access_token = hash.access_token;

    let url =
      'https://accounts.spotify.com/authorize' +    
      '?response_type=token' +
      '&client_id=3f31f6d13b974219a7093698a47d76f9' +
      '&scope=' +
      encodeURIComponent('user-read-private%20user-read-email') +
      '&redirect_uri=' +
      encodeURIComponent('http://localhost:3000');
      window.location = url;
  }

  render() {
    return (

        <Button className="sp_button" onClick={(event) => this.handleRedirect(event)} >
          Link spotify to get started
        </Button>
    );
  }
}

const mapStateToProps = (state) => {
    return {
      vars:state.vars
    }
  };
  
  // mapping the dispatches from our reducer to box props 
  const mapDispatchToProps = (dispatch) => {
    return {
      change: (vName) => dispatch( {type: 'CHANGE', name: vName} ),
  
    }
  
  }

export default connect(mapStateToProps, mapDispatchToProps)(SpotifyAuth);
