import React, {Component} from 'react';
import { Card, Button } from 'react-bootstrap'

class BandPaginator extends Component {
    constructor(props) {
        super(props);
        this.state = {page: 1};
        this.handleIncrement = this.handleIncrement.bind(this);
        this.handleDecrement = this.handleDecrement.bind(this);
    }

    handleIncrement() {
        this.setState({page: (this.state.page + 1) % Math.floor(this.props.bands.length / this.props.perPage )});
    }

    handleDecrement() {
        this.setState({page: Math.abs(this.state.page - 1) % ( this.props.bands.length / this.props.perPage )});
    }

    render() {
        return ( 
        <React.Fragment>
        <div>
            { this.props.bands.slice(this.state.page*this.props.perPage, (this.state.page+1)*this.props.perPage).map((band) =>
                    <Card><h2>{band}</h2></Card> ) }
        </div> 
                <Button onClick={this.handleIncrement}>Next Page</Button>
        </React.Fragment>    
                );

    }

}

export default BandPaginator;
