import React, {Component} from 'react';
import Pagination from 'react-bootstrap/Pagination';
import Card from 'react-bootstrap/Card';

class BandPaginator extends Component {
    constructor(props) {
        super(props);
        let pages = [];
        this.state = {page: 1, pgs: pages};
    }

    componentDidMount() {

        let pages = [];
        for (let i = 0; i < this.props.bands.length / 5; i++) {
            pages.push(<Pagination.Item 
                key={i+1}
                onClick={this.setState({page: i+1}) && console.log(this.state)} 
                active={this.state.page === i+1}>{i+1}</Pagination.Item>);
        }
        this.setState({pgs: pages});
        console.log(this.state)
    }

    render() {
        return ( <div>
        { this.props.bands.slice(this.state.page*5, (this.state.page+1)*5).map((band) =>
                <Card>{band}</Card> ) }
                {JSON.stringify(this.state)}
                {this.state.pgs}

                </div> );
    }

}

export default BandPaginator;
