import React, {Component} from 'react';
import Pagination from 'react-bootstrap/Pagination';
import { Card, Button } from 'react-bootstrap'

class BandPaginator extends Component {
    constructor(props) {
        super(props);
        this.state = {page: 1};
        this.handleIncrement = this.handleIncrement.bind(this);
        this.handleDecrement = this.handleDecrement.bind(this);
    }

    handleIncrement() {
        this.setState({page: (this.state.page + 1) % this.props.bands.length});
    }

    handleDecrement() {
        this.setState({page: Math.abs(this.state.page - 1) % this.props.bands.length });
    }

    render() {
        return ( <div>
        { this.props.bands.slice(this.state.page*5, (this.state.page+1)*5).map((band) =>
                <Card>{band}</Card> ) }
                {JSON.stringify(this.state)}
                <Button onClick={this.handleIncrement}>Next Page</Button>

                </div> );
    }

}

export default BandPaginator;
