import { withRouter } from 'react-router-dom';
import React, { Component } from 'react';
import { Card, Form, Button} from 'react-bootstrap';
import styled from 'styled-components';
import {connect} from 'react-redux';
var client_id = '7d21fa61e53840f8a4ef4d0e1db754b3'; // Your client id
var client_secret = '171c1041af6d4c2d87e7adfcc6cc6ad1'; // Your secret

class SpotifyForm extends Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        const Styles = styled.div``;

    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.props.change({name, value});
        console.log(this.props.vars);
    }

    handleSubmit(e) {
        e.preventDefault();
        fetch('https://mywebsite.com/endpoint/', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                firstParam: 'yourValue',
                secondParam: 'yourOtherValue',
            })
        });
    }


    render() { return (
        <Card>
            <Card.Body>
                <Card.Title>Enter artist's name for recommendations</Card.Title>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group>
                        <Form.Label>Artist</Form.Label>
                        <Form.Control type="text" placeholder="Artist"  name="artist" onChange={this.handleChange} required></Form.Control>
                    </Form.Group>
                    <Form.Group>
                        <Form.Control type="submit" className="bg-primary text-light">

                        </Form.Control>
                    </Form.Group>
                </Form>
            </Card.Body>
        </Card>
    )
    }
}

// mapping our redux state to box props
const mapStateToProps = (state) => {
    return {
      vars:state.vars
    }
  };
  
  // mapping the dispatches from our reducer to box props 
  const mapDispatchToProps = (dispatch) => {
    return {
      change: (vName) => dispatch( {type: 'CHANGE', name: vName} ),
  
    }
  
  }

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(SpotifyForm));
