import React, { Component } from 'react';
import {Button} from 'react-bootstrap';
import {connect} from 'react-redux';

export const authEndpoint = 'https://accounts.spotify.com/authorize';
class SpotifyAuth extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isAuthenticatedWithSpotify: false
      // menu: this.props.userId.menu
    };
    this.state.handleRedirect = this.handleRedirect.bind(this);
  }

  generateRandomString(length) {
    let text = '';
    const possible =
      'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    for (let i = 0; i < length; i++) {
      text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
  }

  getHashParams() {
    const hashParams = {};
    const r = /([^&;=]+)=?([^&;]*)/g;
    const q = window.location.hash.substring(1);
    let e = r.exec(q);
    while (e) {
      hashParams[e[1]] = decodeURIComponent(e[2]);
      e = r.exec(q);
    }
    return hashParams;
  }

  componentDidMount() {
    const params = this.getHashParams();

    const access_token = params.access_token;
    const state = params.state;
    const storedState = localStorage.getItem('stateKey');
    localStorage.setItem('spotifyAuthToken', access_token);
    localStorage.getItem('spotifyAuthToken');

    if (window.localStorage.getItem('authToken')) {
      this.setState({ isAuthenticatedWithSpotify: true });
      console.log(window.localStorage.getItem('authToken'));
    }
    if (access_token && (state == null || state !== storedState)) {
      alert('Click "ok" to finish authentication with Spotify');
    } else {
      localStorage.removeItem('stateKey');
    }
    // handle token
    console.log(storedState);
    
    
  }

  handleRedirect(event) {
    event.preventDefault();

    const params = this.getHashParams();
    const access_token = params.access_token;
    

    const state = this.generateRandomString(16);
    localStorage.setItem('stateKey', state);

    let url =
      'https://accounts.spotify.com/authorize' +    
      '?response_type=code' +
      '&client_id=3f31f6d13b974219a7093698a47d76f9' +
      '&scope=' +
      encodeURIComponent('user-read-private%20user-read-email') +
      '&redirect_uri=' +
      encodeURIComponent('http://localhost:3000');
    window.location = url;
  }

  render() {
    return (

        <Button className="sp_button" onClick={(event) => this.handleRedirect(event)} >
          Link spotify to get started
        </Button>
    );
  }
}

const mapStateToProps = (state) => {
    return {
      vars:state.vars
    }
  };
  
  // mapping the dispatches from our reducer to box props 
  const mapDispatchToProps = (dispatch) => {
    return {
      change: (vName) => dispatch( {type: 'CHANGE', name: vName} ),
  
    }
  
  }

export default connect(mapStateToProps, mapDispatchToProps)(SpotifyAuth);
