import React, {Component} from 'react';
import Pagination from 'react-bootstrap/Pagination';
import { Card } from 'react-bootstrap';

export class BandPaginator extends Component {
    constructor(props) {
        super(props);
        let elements = [];
        let pages = [];
        this.state = ({page: 1, pgs: pages, elems: elements});
        for (let i = 0; i < this.props.bands.length; i++) {
            elements.push(<Card value={this.props.bands[i]} />);    
            if (i % this.props.perPage === 0) {
                pages.push(<Pagination.Item 
                    key={(i / this.props.perPage) + 1}
                    onClick={this.setState({page: i / (this.props.perPage) + 1})} 
                    active={this.state.page === i / (this.props.perPage) + 1}>{(i / this.props.perPage) + 1}</Pagination.Item>);
            }
        }
        this.setState({pgs: pages, elems: elements});

    }

    render() {
        return (
            <React.Fragement>
                {this.state.elems.slice(this.state.page*this.props.perPage, (this.state.page+1)*this.props.perPage)}
                {this.state.pgs}
            </React.Fragement>
        )
    }

}
