import React from 'react'
import SpotifyForm from './components/SpotifyForm';
import SpotifyAuth from './components/SpotifyAuth';
import Text from './components/Text';


import { Col, Row, Jumbotron, Container } from 'react-bootstrap';   


export const Home = () => {
        return 

        <React.Fragment>
            <Container className="p-5">
                {window.location.href.indexOf("accessToken") == -1 ? (

                    <Text className="p-5 text-center">
                        <h1>Crowd-sourced Spotify recommendations</h1>
                        <h3>Unsatisfied with your spotify recommendations? I was too; that's why I made this website. This site delivers recommendations based on 
                            <b> playlist adjacency</b> above all. That is, I give you a set of new artists who appear in playlists with a given artist at a high frequency,
                            all ranked by the number of times they are in the same playlists as the artist you entered. 
                        </h3>
                        <SpotifyAuth></SpotifyAuth>
                    </Text>

                )  : 
                <SpotifyForm></SpotifyForm>
                }

            </Container>
            </React.Fragment>
}
