import React from 'react';
import { useStore } from 'react-redux';



class ClassInfo extends Component {
    render() {
        const store = useStore();
        return (
        
        <React.Fragment>
            <h2>Here's some info about your class</h2>
            <div>{store.getState()}</div>
        </React.Fragment>
        )
    }
    
}

export default ClassInfo
