import { withRouter } from 'react-router-dom';
import React, { Component } from 'react';
import { Card, Form, Button, Alert, Row} from 'react-bootstrap';
import styled from 'styled-components';
import {connect} from 'react-redux';
import SpotifyWebApi from 'spotify-web-api-js';

var spotify = new SpotifyWebApi();


class SpotifyForm extends Component {
    constructor(props) {
        super(props);
        this.state = {resultReceived: false, recOutput: {}};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.processResults = this.processResults.bind(this);
        this.getRecs = this.getRecs.bind(this);
        this.createPlaylist = this.createPlaylist.bind(this);
        this.countTracks = this.countTracks.bind(this);
        const Styles = styled.div``;        
    }
    
    /**
     * Handle change in the artist value
     * @param {Object} event Event incurred by change 
     */
    handleChange(event) {
        console.log(this.state)
        this.setState({resultReceived: false});
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({[name]: value});
        this.render();
    }

    /**
     * Handle errors by updating the state
     * @param {Object} err 
     */
    handleError(err) {
        
        switch (err.status) {
            case 401:
                this.setState({errorStatus: "You don't seem to be authorized. Try clicking the button above to authorize with Spotify and try again."});
                break;
            case 429:
                this.setState({errorStatus: "Looks like we're making too many requests to Spotify right now. Wait a little bit and try that again."})
                break; 
            default:
                this.setState({errorStatus: "Some error occurred. Please try again."});
        }

    }

    async countTracks(id, name, output) {
        const MAX_SONGS = 50;
        return spotify.getPlaylistTracks(id).then(
            async (pt) => {
                let curSongs = 0;
                if (pt == undefined) {
                    return;
                }
                for (let track of pt.items) {
                    if (curSongs > MAX_SONGS) break;
                    if (track.track != null 
                        && track.track.artists[0].name !== name
                        && track.track.artists[0].name !== "") {
                        if (track.track.artists[0].name in output) {
                            output[track.track.artists[0].name]++;
                        } else {
                            output[track.track.artists[0].name] = 1;
                        }
                    }
                    curSongs++;
                }

            }).catch(err => this.handleError(err));

    }
    /**
     * Gets recommendations for a specific artist and outputs recOutput value in redux store
     * @param {string} name The name of the artist
     * TODO: catch errors again lol
     * /
    **/
   async getRecs(name) {
       debugger;
        var output = {};
        
        let promises = [];
        await spotify.searchPlaylists(name, {limit: 1})
        .then(async (searchTotal) => 
            await spotify.searchPlaylists(name, {limit: 50, offset: Math.floor(Math.random() * (searchTotal.playlists.total/50))}).then(
                res => {
                    for (let i of res.playlists.items) {
                            promises.push(this.countTracks(i.id, name, output));

                    }
                } 
            )
        ).catch(err => this.handleError(err));

        Promise.all(promises).then(() => this.setState({recOutput: this.processResults(output)}));
    
        
    }
    /**
     * Processesresults from our query to spotify, removing entries beyond a certain threshhold then sorting the object.
     * @return Results
     */
    processResults(input) {
        let processed = {};
        for (let key in input)   {
            if (input[key] > 10) {
                processed[key] = input[key];
            }
        }
        const sorted = Object.fromEntries(
            Object.entries(processed).sort(([,a],[,b]) => b-a)
        );
        return sorted;
    }

    /**
     * Creates playlist with one random song from each output artist
     */
    createPlaylist () {
        let uris = [];

        //spotify.createPlaylist().then((pl) =>
            
       // );
    }

    /**
     *  Handles submission of Artist form
     * @param {Object} e Submission event
     * TODO: Error handling
     */
    handleSubmit(e) {        
        e.preventDefault();
        spotify.setAccessToken(this.props.vars.token);
        spotify.searchArtists(this.state.artist)
        .then(res => this.getRecs(res.artists.items[0].name))
        .then(() => this.setState({resultReceived: true}))
        .catch(err => this.handleError(err));
        
    }

    


    render() {
        
            return (
                <React.Fragment>
                {this.state.errorStatus !== undefined ? <Alert className="alert-primary">{this.state.errorStatus}</Alert> : null }
                    <Card>
                        <Card.Body>
                            <Card.Title>Enter artist's name for recommendations</Card.Title>
                            <Form onSubmit={this.handleSubmit}>
                                <Form.Group>
                                    <Form.Label>Artist</Form.Label>
                                    <Form.Control type="text" placeholder="Iron Maiden"  name="artist" onChange={this.handleChange} required></Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Control type="submit" className="bg-primary text-light col-8 offset-2">
            
                                    </Form.Control>
                                </Form.Group>
                                <Form.Group>
                                    <Form.Control type="reset" className="bg-primary text-light col-8 offset-2">
            
                                    </Form.Control>
                                </Form.Group>
                            </Form>
                        </Card.Body>
                    </Card>
                <Row>
                    <div className="d-block">
                    {this.state.resultReceived  ? <h4>Recommended Artists Based on {this.state.artist}</h4> : null }
                    <ul>{this.state.resultReceived ? Object.keys(this.state.recOutput).map(band => <li>{band}: {this.state.recOutput[band]}</li>) : null }</ul>
                    {this.state.resultReceived  ? <Button className="mt-3" onClick={this.createPlaylist()}>Create a playlist with songs from these artists</Button> : null} 
                    </div>
                </Row>
                </React.Fragment>
                
                
            )
        
    }
}

// mapping our redux state to box props
const mapStateToProps = (state) => {
    return {
      vars:state.vars
    }
  };
  
  // mapping the dispatches from our reducer to box props 
  const mapDispatchToProps = (dispatch) => {
    return {
      change: (vName) => dispatch( {type: 'CHANGE', name: vName} ),
  
    }
  
  }

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(SpotifyForm));
