import React, {Component} from 'react';
import Pagination from 'react-bootstrap/Pagination';
import Card from 'react-bootstrap/Card';

class BandPaginator extends Component {
    constructor(props) {
        super(props);
        let elements = [];
        let pages = [];
        this.state = ({page: 1, pgs: pages, elems: elements});
        for (let i = 0; i < this.props.bands.length; i++) {
            elements.push(<Card value={this.props.bands[i]} />);    
            if (i % 5 === 0) {
                pages.push(<Pagination.Item 
                    key={(i / 5) + 1}
                    onClick={this.setState({page: i / (5) + 1})} 
                    active={this.state.page === i / (5) + 1}>{(i / 5) + 1}</Pagination.Item>);
            }
        }
        this.setState({pgs: pages, elems: elements});

    }

    render() {
        return (
            <div>

                this.state.elems.slice(this.state.page*5, (this.state.page+1)*5)
                this.state.pages
            </div>

        )
    }

}

export default BandPaginator;
